tql.tql:
SELECT * FROM slackbot.post_chat_message
  WHERE $body=(SELECT {
  	'link_names' : true,
    'channel' : @userid,
  	'as_user' : true,
    'attachments' : [
     {
       'title': 'Message clipped!',
       -- This should be the link in the ClipIt web app
       'title_link': @url,
       'fields': [
         {
           'title': 'Message',
           'value': @message
         },
         {
           'title': 'Posted by',
           'value': @poster,
           'short': true
         },
         {
           'title': 'Importance',
           'value': @importance,
           'short': true
         },
       ],
     },
   ]
  })

SELECT * FROM slackbot.open_dialog
  WHERE $body=(SELECT {
    'dialog' : {
      'title': 'Submit a helpdesk ticket',
      'callback_id': 'submit-ticket',
      'submit_label': 'Submit',
      'elements': [
        {
          'label': 'Title',
          'type': 'text',
          'name': 'title',
          'value': @text,
          'hint': '30 second summary of the problem',
        },
        {
          'label': 'Description',
          'type': 'textarea',
          'name': 'description',
          'optional': true,
        },
        {
          'label': 'Urgency',
          'type': 'select',
          'name': 'urgency',
          'options': [
            { 'label': 'Low', 'value': 'Low' },
            { 'label': 'Medium', 'value': 'Medium' },
            { 'label': 'High', 'value': 'High' },
          ],
        },
      ]
    },
    'trigger_id' : @trigger_id
  })
