tql.tql:
SELECT * FROM slack.post_chat_message
  WHERE $body=(SELECT {
  	'link_names' : true,
    'channel' : @userid,
  	'username' : 'ClipIt',
    'attachments' : [
     {
       'title': 'Message clipped!',
       -- This should be the link in the ClipIt web app
       'title_link': @url,
       'fields': [
         {
           'title': 'Message',
           'value': @message
         },
         {
           'title': 'Posted by',
           'value': @poster,
           'short': true
         },
         {
           'title': 'Importance',
           'value': @importance,
           'short': true
         },
       ],
     },
   ]
  })
